import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import GifList from './gifLİst';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      gifs: []
    };
  } 

  componentDidMount() {
    fetch(this.props.dataURL)
      .then(response => response.json())
      .then(responseData => {
        this.setState({ gifs: responseData.data });
      })
      .catch(error => {
        console.log('Error fetching and parsing data', error);
      });
  }

  render() {
    console.log(this.state.gifs);
    return (
      <div>
        <div className="header">
          <img className="header-logo" src="giphyLogo.jpeg" alt="backgroundImage" />
          <input className="search-area" type="text" placeholder="Search all the GIFs :)"></input>
          <button className="search-button"><i className={`fa fa-arrow-left left-icon`} onClick={this.goToPrevSlide}></i></button>
        </div>
        <GifList data={this.state.gifs} />
      </div>
    );
  }
}

ReactDOM.render(
  <App dataURL='https://api.giphy.com/v1/gifs/trending?api_key=dc6zaTOxFJmzC'/>,
  document.getElementById('root')
);

export default App;
