import React from 'react';
import './App.css';
import Gif from './gif';
import NoGifs from './noGifs';


class GifList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
       loadingState: false,
       offset: props.offset
     };
   }

  componentWillReceiveProps(nextProps) {
    if(nextProps.offset !== this.props.offset) {
      this.setState({
        offset: nextProps.offset,
      })
    }
  }

  componentDidMount() {
    this.refs.iScroll.addEventListener("scroll", () => {
      if (this.refs.iScroll.scrollTop + this.refs.iScroll.clientHeight >= this.refs.iScroll.scrollHeight - 200){
        this.loadMoreItems();
      }
    });
  }

  loadMoreItems() {
    const { data, setGiflist } = this.props;

    if(this.state.loadingState){
      return;
    }

    this.setState(prevState => ({
      offset: prevState.offset + 1,
    }));

    setTimeout(() => {
      fetch('https://api.giphy.com/v1/gifs/trending?&offset=' + this.state.offset + '&limit=20&api_key=dc6zaTOxFJmzC')
      .then(response => response.json())
      .then(responseData => {
        setGiflist(data.concat(responseData.data))
      })
      .catch(error => {
        console.log(error);
      }); 
      this.setState({ gifs: this.props.gifs, loadingState: false });
    }, 1000);
  }

  render() {
    const results = this.props.data;
    let gifs;
    if(results.length > 0 ){
      gifs = results.map( gif => <Gif url={gif.images.fixed_height.url} key={gif.id} /> );
    }
    else{
      gifs= <NoGifs/>
    }

    return (
      <div ref="iScroll" style={{ height: "100vh", overflow: "auto" }}>
        <ul className="gif-list">
          { gifs }
        </ul>
        {this.state.loadingState ? <p className="loading"> loading More Items..</p> : ""}
      </div>
    );
  }
}

export default GifList;
