import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import GifList from './gifLİst';

class App extends React.Component {
  constructor(props) {
    super(props);
   this.state = {
     gifs: []
   }
  }

  componentDidMount() {   
    this.getTrend();
  }

  getTrend() {
    fetch('https://api.giphy.com/v1/gifs/trending?api_key=dc6zaTOxFJmzC')
      .then(response => response.json())
      .then(responseData => {
        this.setState({gifs: responseData.data });
      })
      .catch(error => {
        console.log(error);
      });  
  }

  performSearch(query) {
    fetch(`https://api.giphy.com/v1/gifs/search?q=${query}&limit=24&api_key=dc6zaTOxFJmzC`)
      .then(response => response.json())
      .then(responseData => {
        this.setState({gifs: responseData.data });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    console.log(this.state.gifs);
    return (
      <div>
        <div className="header">
          <img className="header-logo" src="giphyLogo.jpeg" alt="headerLogo" />
          <input className="search-area" type="text" placeholder="  Search all the GIFs :)"></input>
          <button className="search-button" onClick={() => performSearch(query)}>
            <img className="search-logo" src="search.png" alt="searchLogo" />
          </button>
        </div>
        <GifList data={this.state.gifs} />
      </div>
    );
  }
}

ReactDOM.render(
  <App dataURL='https://api.giphy.com/v1/gifs/trending?api_key=dc6zaTOxFJmzC'/>,
  document.getElementById('root')
);

export default App;
